import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Card, Button, Row, Col, Spinner } from 'react-bootstrap';
import axios from 'axios';
import { LinkContainer } from 'react-router-bootstrap';
import '../styles/blogStyles.css';

const UserBlogsScreen = () => {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserBlogs = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/login');
          return;
        }

        const config = {
          headers: {
            'x-auth-token': token
          }
        };

        const { data } = await axios.get('/api/blogs', config);
        // Filter blogs by current user (this would ideally be done on the backend)
        const userData = await axios.get('/api/users/me', config);
        const userBlogs = data.blogs.filter(blog => blog.author._id === userData.data._id);
        setBlogs(userBlogs);
        setLoading(false);
      } catch (error) {
        setError(error.response?.data?.msg || 'Error fetching blogs');
        setLoading(false);
      }
    };

    fetchUserBlogs();
  }, [navigate]);

  if (loading) return <Spinner animation="border" />;
  if (error) return <div>{error}</div>;

  return (
    <Container className="blog-container">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h1>My Blogs</h1>
        <Button as={Link} to="/create" variant="primary" className="btn-create">
          Create New Blog
        </Button>
      </div>
      
      {loading ? (
        <Spinner animation="border" />
      ) : error ? (
        <div className="alert alert-danger">{error}</div>
      ) : blogs.length === 0 ? (
        <div className="text-center py-4">
          <h4>You haven't written any blogs yet.</h4>
          <Button as={Link} to="/create" variant="primary" className="btn-create mt-3">
            Create Your First Blog
          </Button>
        </div>
      ) : (
        <Row>
          {blogs.map((blog) => (
            <Col key={blog._id} sm={12} md={6} lg={4}>
              <Card className="blog-card">
                <Card.Body>
                  <Card.Title className="blog-title">{blog.title}</Card.Title>
                  <Card.Text className="blog-content">
                    {blog.content.substring(0, 100)}...
                  </Card.Text>
                  <div className="d-flex">
                    <Button 
                      as={Link} 
                      to={`/blog/${blog._id}`}
                      variant="primary" 
                      className="blog-btn btn-view"
                    >
                      View
                    </Button>
                    <Button 
                      as={Link} 
                      to={`/edit/${blog._id}`}
                      variant="success" 
                      className="blog-btn btn-edit"
                    >
                      Edit
                    </Button>
                  </div>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      )}
    </Container>
  );
};

export default UserBlogsScreen;
