import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { Button, Card, ListGroup, Spinner } from 'react-bootstrap';
import axios from 'axios';
import { LinkContainer } from 'react-router-bootstrap';

const BlogScreen = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [blog, setBlog] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [userInfo, setUserInfo] = useState(null);

  useEffect(() => {
    const fetchBlog = async () => {
      try {
        const { data } = await axios.get(`/api/blogs/${id}`);
        setBlog(data);
        setLoading(false);
        
        // Check if user is logged in
        const token = localStorage.getItem('token');
        if (token) {
          try {
            const config = {
              headers: {
                'x-auth-token': token
              }
            };
            const { data: userData } = await axios.get('/api/users/me', config);
            setUserInfo(userData);
          } catch (error) {
            console.error('Error fetching user info:', error);
          }
        }
      } catch (error) {
        setError(error.response?.data?.msg || 'Error fetching blog');
        setLoading(false);
      }
    };

    fetchBlog();
  }, [id]);

  const handleDelete = async () => {
    if (!window.confirm('Are you sure you want to delete this blog?')) {
      return;
    }
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      const config = {
        headers: {
          'x-auth-token': token,
          'Content-Type': 'application/json'
        }
      };

      const { data } = await axios.delete(`/api/blogs/${id}`, config);
    
    if (data.success) {
      // Show success message
      alert('Blog deleted successfully!');
      navigate('/');
    } else {
      throw new Error(data.message || 'Failed to delete blog');
    }
  } catch (error) {
    console.error('Delete error:', error);
    alert(error.response?.data?.message || error.message || 'Failed to delete blog');
  }
};

  if (loading) return <Spinner animation="border" />;
  if (error) return <div>{error}</div>;

  return (
    <Card>
      <Card.Body>
        <Card.Title>{blog.title}</Card.Title>
        <Card.Subtitle className="mb-2 text-muted">
          By {blog.author.email}
        </Card.Subtitle>
        <Card.Text>{blog.content}</Card.Text>
        <Card.Footer>
          <small className="text-muted">
            Posted on {new Date(blog.createdAt).toLocaleDateString()}
          </small>
        </Card.Footer>
        {userInfo && userInfo._id === blog.author._id && (
          <div className="mt-3">
            <Button 
              as={Link} 
              to={`/edit/${blog._id}`}
              variant="primary" 
              className="me-2">
              Edit
            </Button>
            <Button variant="danger" onClick={handleDelete}>
              Delete
            </Button>
          </div>
        )}
      </Card.Body>
    </Card>
  );
};

export default BlogScreen;
