import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Card, Button, Row, Col, Spinner } from 'react-bootstrap';
import axios from 'axios';
import { LinkContainer } from 'react-router-bootstrap';

const UserBlogsScreen = () => {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserBlogs = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/login');
          return;
        }

        const config = {
          headers: {
            'x-auth-token': token
          }
        };

        const { data } = await axios.get('/api/blogs', config);
        // Filter blogs by current user (this would ideally be done on the backend)
        const userData = await axios.get('/api/users/me', config);
        const userBlogs = data.blogs.filter(blog => blog.author._id === userData.data._id);
        setBlogs(userBlogs);
        setLoading(false);
      } catch (error) {
        setError(error.response?.data?.msg || 'Error fetching blogs');
        setLoading(false);
      }
    };

    fetchUserBlogs();
  }, [navigate]);

  if (loading) return <Spinner animation="border" />;
  if (error) return <div>{error}</div>;

  return (
    <>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h1>My Blogs</h1>
        <Link to="/create">
          <Button variant="primary">Create New Blog</Button>
        </Link>
      </div>
      <Row>
        {blogs.length === 0 ? (
          <Col>
            <p>You haven't written any blogs yet.</p>
          </Col>
        ) : (
          blogs.map((blog) => (
            <Col key={blog._id} sm={12} md={6} lg={4}>
              <Card className="my-3">
                <Card.Body>
                  <Card.Title>{blog.title}</Card.Title>
                  <Card.Text>
                    {blog.content.substring(0, 100)}...
                  </Card.Text>
                  <LinkContainer to={`/blog/${blog._id}`}>
                    <Button variant="primary" className="me-2">
                      View
                    </Button>
                  </LinkContainer>
                  <LinkContainer to={`/edit/${blog._id}`}>
                    <Button variant="secondary">
                      Edit
                    </Button>
                  </LinkContainer>
                </Card.Body>
              </Card>
            </Col>
          ))
        )}
      </Row>
    </>
  );
};

export default UserBlogsScreen;
