import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { Button, Card, ListGroup, Spinner } from 'react-bootstrap';
import axios from 'axios';
import { LinkContainer } from 'react-router-bootstrap';

const BlogScreen = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [blog, setBlog] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [userInfo, setUserInfo] = useState(null);

  useEffect(() => {
    const fetchBlog = async () => {
      try {
        const { data } = await axios.get(`/api/blogs/${id}`);
        setBlog(data);
        setLoading(false);
        
        // Check if user is logged in
        const token = localStorage.getItem('token');
        if (token) {
          try {
            const config = {
              headers: {
                'x-auth-token': token
              }
            };
            const { data: userData } = await axios.get('/api/users/me', config);
            setUserInfo(userData);
          } catch (error) {
            console.error('Error fetching user info:', error);
          }
        }
      } catch (error) {
        setError(error.response?.data?.msg || 'Error fetching blog');
        setLoading(false);
      }
    };

    fetchBlog();
  }, [id]);

  const handleDelete = async () => {
    if (!window.confirm('Are you sure you want to delete this blog?')) {
      return;
    }
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      const config = {
        headers: {
          'x-auth-token': token,
          'Content-Type': 'application/json'
        }
      };

      const { data } = await axios.delete(`/api/blogs/${id}`, config);
    
    if (data.success) {
      // Show success message
      alert('Blog deleted successfully!');
      navigate('/');
    } else {
      throw new Error(data.message || 'Failed to delete blog');
    }
  } catch (error) {
    console.error('Delete error:', error);
    alert(error.response?.data?.message || error.message || 'Failed to delete blog');
  }
};

if (loading) return (
  <div className="flex justify-center items-center h-screen">
    <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
  </div>
);

if (error) return (
  <div className="p-4">
    <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">
      {error}
    </div>
  </div>
);

return (
  <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
    <div className="bg-white rounded-lg shadow-md overflow-hidden">
      <div className="p-6">
        <h1 className="text-2xl font-bold text-gray-900 mb-2">{blog.title}</h1>
        <p className="text-gray-600 mb-4">By {blog.author.email}</p>
        
        <div className="prose max-w-none mb-6">
          {blog.content}
        </div>
        
        <p className="text-sm text-gray-500 mb-6">
          Posted on {new Date(blog.createdAt).toLocaleDateString()}
        </p>
        
        {userInfo && userInfo._id === blog.author._id && (
          <div className="flex space-x-3">
            <Link
              to={`/edit/${blog._id}`}
              className="bg-blue-600 hover:bg-blue-700 text-white font-medium py-2 px-4 rounded transition duration-200"
            >
              Edit
            </Link>
            <button
              onClick={handleDelete}
              className="bg-red-600 hover:bg-red-700 text-white font-medium py-2 px-4 rounded transition duration-200"
            >
              Delete
            </button>
          </div>
        )}
      </div>
    </div>
  </div>
);
};

export default BlogScreen;
