import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Form, Button, Alert, Spinner } from 'react-bootstrap';
import axios from 'axios';

const EditBlogScreen = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBlog = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/login');
          return;
        }

        const config = {
          headers: {
            'x-auth-token': token
          }
        };

        const { data } = await axios.get(`/api/blogs/${id}`, config);
        
        // Check if current user is the author
        const userData = await axios.get('/api/users/me', config);
        if (data.author._id !== userData.data._id) {
          navigate('/');
          return;
        }

        setTitle(data.title);
        setContent(data.content);
        setLoading(false);
      } catch (err) {
        setError(err.response?.data?.msg || 'Error fetching blog');
        setLoading(false);
      }
    };

    fetchBlog();
  }, [id, navigate]);

  const submitHandler = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: {
          'x-auth-token': token
        }
      };

      await axios.put(
        `/api/blogs/${id}`,
        { title, content },
        config
      );
      navigate('/myblogs');
    } catch (err) {
      setError(err.response?.data?.msg || 'Error updating blog');
    }
  };

  if (loading) return <Spinner animation="border" />;
  if (error) return <div>{error}</div>;

  return (
    <>
      <h1>Edit Blog</h1>
      {error && <Alert variant="danger">{error}</Alert>}
      <Form onSubmit={submitHandler}>
        <Form.Group controlId="title" className="my-3">
          <Form.Label>Title</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </Form.Group>

        <Form.Group controlId="content" className="my-3">
          <Form.Label>Content</Form.Label>
          <Form.Control
            as="textarea"
            rows={8}
            placeholder="Enter content"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            required
          />
        </Form.Group>

        <Button type="submit" variant="primary" className="my-3">
          Update
        </Button>
      </Form>
    </>
  );
};

export default EditBlogScreen;
