import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { Row, Col, Card, Button, Pagination } from 'react-bootstrap';
import axios from 'axios';
import { LinkContainer } from 'react-router-bootstrap';

const HomeScreen = () => {
  const [blogs, setBlogs] = useState([]);
  const [page, setPage] = useState(1);
  const [pages, setPages] = useState(1);
  const [total, setTotal] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        const { data } = await axios.get(`/api/blogs?page=${page}`);
        setBlogs(data.blogs);
        setPages(data.totalPages);
        setTotal(data.totalBlogs);
      } catch (error) {
        console.error('Error fetching blogs:', error);
      }
    };

    fetchBlogs();
  }, [page]);

  return (
    <>
      <h1>Latest Blogs</h1>
      <Row>
        {blogs.map((blog) => (
          <Col key={blog._id} sm={12} md={6} lg={4} xl={3}>
            <Card className="my-3 p-3 rounded">
              <Card.Body>
              <Card.Title as={Link} to={`/blog/${blog._id}`}>
                  <strong>{blog.title}</strong>
                </Card.Title>
                <Card.Text as="div">
                  <div className="my-3">
                    {blog.content.substring(0, 100)}...
                  </div>
                </Card.Text>
                <Card.Text as="div">
                  By {blog.author.email}
                </Card.Text>
                <Button as={Link} to={`/blog/${blog._id}`} variant="primary">
                    Read More
                  </Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
      {pages > 1 && (
        <Pagination>
          {[...Array(pages).keys()].map((x) => (
            <Pagination.Item
              key={x + 1}
              active={x + 1 === page}
              onClick={() => setPage(x + 1)}
            >
              {x + 1}
            </Pagination.Item>
          ))}
        </Pagination>
      )}
    </>
  );
};

export default HomeScreen;
