import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Card, Button, Row, Col, Spinner } from 'react-bootstrap';
import axios from 'axios';

const UserBlogsScreen = () => {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserBlogs = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/login');
          return;
        }

        const config = {
          headers: {
            'x-auth-token': token
          }
        };

        const { data } = await axios.get('/api/blogs', config);
        const userData = await axios.get('/api/users/me', config);
        const userBlogs = data.blogs.filter(blog => blog.author._id === userData.data._id);
        setBlogs(userBlogs);
        setLoading(false);
      } catch (error) {
        setError(error.response?.data?.msg || 'Error fetching blogs');
        setLoading(false);
      }
    };

    fetchUserBlogs();
  }, [navigate]);

  if (loading) return (
    <div className="flex justify-center items-center h-screen">
      <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
    </div>
  );

  if (error) return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">
        {error}
      </div>
    </div>
  );

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold text-gray-900">My Blogs</h1>
        <Link 
          to="/create" 
          className="bg-purple-600 hover:bg-purple-700 text-white font-medium py-2 px-4 rounded-lg transition duration-200"
        >
          Create New Blog
        </Link>
      </div>
      
      {blogs.length === 0 ? (
        <div className="text-center py-12">
          <h4 className="text-xl text-gray-600 mb-4">You haven't written any blogs yet.</h4>
          <Link 
            to="/create" 
            className="bg-purple-600 hover:bg-purple-700 text-white font-medium py-2 px-4 rounded-lg transition duration-200"
          >
            Create Your First Blog
          </Link>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {blogs.map((blog) => (
            <div key={blog._id} className="bg-white rounded-lg shadow-md overflow-hidden transition-transform duration-300 hover:-translate-y-1 hover:shadow-lg">
              <div className="p-6">
                <h2 className="text-xl font-semibold text-gray-800 mb-3">{blog.title}</h2>
                <p className="text-gray-600 mb-4 line-clamp-3">
                  {blog.content.substring(0, 100)}...
                </p>
                <div className="flex space-x-3">
                  <Link 
                    to={`/blog/${blog._id}`}
                    className="bg-blue-600 hover:bg-blue-700 text-white font-medium py-1 px-3 rounded transition duration-200"
                  >
                    View
                  </Link>
                  <Link 
                    to={`/edit/${blog._id}`}
                    className="bg-green-600 hover:bg-green-700 text-white font-medium py-1 px-3 rounded transition duration-200"
                  >
                    Edit
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default UserBlogsScreen;
